package com.future.demo.java.apple.pay;

import com.alibaba.fastjson.JSONObject;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.util.CollectionUtils;
import org.springframework.util.StringUtils;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import java.util.*;

/**
 *
 */
@RestController
@RequestMapping(value = "/api/v1/order")
public class ApiController {
    @Autowired
    ApplePayOrderRepository applePayOrderRepository = null;

    @RequestMapping(value = "create", method = RequestMethod.POST, produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Map<String, Object>> create(
            /*@RequestParam(value = "productId", defaultValue = "") String productId*/) {
        Map<String, Object> responseMap = new HashMap<>();
//        if(StringUtils.isEmpty(productId)) {
//            return responseWithError("请指定产品");
//        }

        String orderId = "ORD" + UUID.randomUUID().toString();
        ApplePayOrderModel model = new ApplePayOrderModel();
        model.setOrderId(orderId);
//        model.setProductId(productId);
        model.setStatus(TransactionStatus.PENDING);
        model.setCreateTime(new Date());
        this.applePayOrderRepository.save(model);
        responseMap.put("data", orderId);
        return ResponseEntity.ok(responseMap);
    }

    public ResponseEntity<Map<String, Object>> responseWithError(String errorMessage) {
        Map<String, Object> responseMap = new HashMap<>();
        responseMap.put("errorCode", 5000);
        responseMap.put("errorMessage", errorMessage);
        return ResponseEntity.ok(responseMap);
    }

    public ResponseEntity<Map<String, Object>> responseWithSuccess(Object data) {
        Map<String, Object> responseMap = new HashMap<>();
        responseMap.put("data", data);
        return ResponseEntity.ok(responseMap);
    }

    @RequestMapping(value = "verify", method = RequestMethod.POST, produces = MediaType.APPLICATION_JSON_VALUE)
    public ResponseEntity<Map<String, Object>> verify(
            @RequestParam(value = "receipt", defaultValue = "") String receipt) {
        receipt = "";
        System.out.println("苹果内购校验开始，base64校验体：" + receipt);

        //线上环境验证
        String verifyResult = IosVerifyUtil.buyAppVerify(receipt, 1);
        if (verifyResult == null) {
            return responseWithError("苹果验证失败，返回数据为空");
        } else {
            System.out.println("线上，苹果平台返回JSON:" + verifyResult);
            JSONObject appleReturn = JSONObject.parseObject(verifyResult);
            String states = appleReturn.getString("status");
            //无数据则沙箱环境验证
            if ("21007".equals(states)) {
                verifyResult = IosVerifyUtil.buyAppVerify(receipt, 0);
                System.out.println("沙盒环境，苹果平台返回JSON:" + verifyResult);
                appleReturn = JSONObject.parseObject(verifyResult);
                states = appleReturn.getString("status");
            }
            System.out.println("苹果平台返回值：appleReturn" + appleReturn);
            // 前端所提供的收据是有效的    验证成功
            if (states.equals("0")) {
                receipt = appleReturn.getString("receipt");
                JSONObject returnJson = JSONObject.parseObject(receipt);
                String inApp = returnJson.getString("in_app");
                List<HashMap> inApps = JSONObject.parseArray(inApp, HashMap.class);
                if (!CollectionUtils.isEmpty(inApps)) {
                    String orderId = null;
                    for (HashMap app : inApps) {
                        orderId = (String) app.get("transaction_id");
                    }
                    //交易列表包含当前交易，则认为交易成功
                    if (this.applePayOrderRepository.findByOrderId(orderId) != null) {
                        System.out.println("交易成功，新增并处理订单：" + orderId);
                        return responseWithSuccess("充值成功");
                    }
                    return responseWithError("当前交易不在交易列表中");
                }
                return responseWithError("未能获取获取到交易列表");
            } else {
                return responseWithError("支付失败，错误码：" + states);
            }
        }
    }
}
